
public class 연산자응용문제풀이3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=5;
		int c=5;
		System.out.println("a==b->"+(a==b));
		System.out.println("a!=b->"+(a!=b));
		System.out.println("a>b->"+(a>b));
		System.out.println("b>=c->"+(b>=c));
		System.out.println("a<b->"+(a<b));
		System.out.println("b<=c->"+(b<=c));

	}

}
